import React from 'react';
import './Widget.css';

class Widget extends React.Component {
  constructor(props) {
    super(props);
    this.state = { width: 0 };
    this.updateWindowDimensions = this.updateWindowDimensions.bind(this);
  }

  componentDidMount() {
    this.updateWindowDimensions();
    window.addEventListener('resize', this.updateWindowDimensions);
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.updateWindowDimensions);
  }

  updateWindowDimensions() {
    this.setState({ width: window.innerWidth });
  }

  render() {

    return (
      <div className='outer'>
        <div className='inner-padding'
          style={
            this.state.width > 600 ?
              {
                flex: 'none',
                maxWidth: '90%',
                textAlign: 'center',
                position: 'relative',
                border: '4px solid rgba(166,128,184,1)',
                padding: '100px'
              } : {
                flex: 'none',
                maxWidth: '90%',
                textAlign: 'center',
                position: 'relative',
              }
            }>

            <section style={{ minHeight: '120px' }}>
              <img className='image-style' src={this.props.image} alt={this.props.alt}/>
            </section>

            <section>
              <h2 className='question'>{this.props.question}</h2>
              <input className='answer' type='text' placeholder={this.props.placeholder}/>
            </section>

            <section className='buttons'>
              <button className='btn-container back-btn btn'>
                { this.props.previousExists ? 'Back' : '' }
              </button>

              <button className='btn-container next-btn btn'>
                { this.props.isLast ? 'Complete' : 'Next' }
              </button>

            </section>

        </div>
      </div>
    );
  }

}

export default Widget
