import React, { Component } from 'react';
import Widget from './Widget';
import cards from '../data/cards';

class Wizard extends Component {
  constructor(props) {
    super(props);
    this.state = { current: 0 }
    this.handleNext = this.handleNext.bind(this)
    this.handlePrev = this.handlePrev.bind(this)
  }

  handleNext() {
    console.log('next')
  }

  handlePrev() {
    console.log('previous')
  }

  render() {
    console.log(cards)
    return (
      <div>
          <Widget
            question={cards[this.state.current].question}
            image={cards[this.state.current].image}
            placeholder={cards[this.state.current].placeholder}
            isLast={() => {
              if (cards.length === this.state.current - 1) { return true }
            }}
            previousExists={() => this.state.current === 0}
            handleNext={() => this.handleNext() }
            handlePrev={() => this.handlePrev() }
          />
      </div>
    );
  }
}

export default Wizard;
