import React from 'react';
import './Widget.css';

const outer = {
  height: '100vh',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center'
};

const inner = {
  flex: 'none'
};


const Widget = (props) => (

  <div style={ outer }>
    <div style={ inner }>

      <section className='image-section' style={{ minHeight: '120px' }}>
        <img className='image-style' src={props.image} alt={props.alt}/>
      </section>

      <section className='qa-section'>
        <h2 id='question'>{props.question}</h2>
        <input id='answer' type='text' placeholder={props.placeholder}/>
      </section>

      <section className='button-section'>
        <div className='leftBtn'>
          {props.previousExists ?
            <button className='btn backBtn'>Back</button>
          : ''}
        </div>
        <div className='rightBtn'>
          {props.isLast ?
            <button className='btn nextBtn'>Complete</button> :
            <button className='btn nextBtn'>Next</button>
          }
        </div>
      </section>

    </div>
  </div>
);

export default Widget
