import React from 'react';
import './Widget.css';

const outer = {
  width: '300px',
  margin: 'auto',
  textAlign: 'center'
};

const inner = {
  margin: '100px 0 0 0',
  textAlign: 'center'
};

const imageStyle = { width: '100px' };

const questionStyle = {
  margin: '30px 0 0 0',
  width: '100%'
};

const inputStyle = {
  margin: '40px 0 0 0',
  padding: '2% 5%',
  width: '90%',
  height: '40px',
  fontSize: '16px'
};

const buttonSection = {
  margin: '40px 0 0 0',
  position: 'relative'
};

const leftBtn = {
  position: 'absolute',
  left: 0
};

const rightBtn = {
  position: 'absolute',
  right: 0
};

const backBtn = {
  background: 'rgba(213,232,212,1)',
  border: 'solid 2px rgba(130,179,102,1)'
}

const completeBtn = {}

const nextBtn = {
  background: 'rgba(213,232,212,1)',
  border: 'solid 2px rgba(130,179,102,1)'
}

const Widget = (props) => (
  <div style={ outer }>
    <div style={ inner }>
      <img style={ imageStyle } src={props.image} alt={props.alt}/>
      <h2 style={ questionStyle }>{props.question}</h2>
      <input style={ inputStyle } type='text' />
      <div style={ buttonSection }>
        <div style={ leftBtn }>
          {props.previousExists ?
            <button style={ backBtn } className='btn'>Back</button>
          : ''}
        </div>
        <div style={ rightBtn }>
          {props.isLast ?
            <button style={ completeBtn } className='btn'>Complete</button> :
            <button style={ nextBtn } className='btn'>Next</button>
          }
        </div>
      </div>
    </div>
  </div>
);

export default Widget
