import React from 'react';
import './Widget.css';

const Widget = (props) => (
  <div style={ outerStyle }>
    <div style={ innerStyle }>
      <img style={ imageStyle } src={props.image} alt={props.alt}/>
      <h2 style={ questionStyle }>{props.question}</h2>
      <input style={ inputStyle } type='text' />
      <div style={ buttonSection }>
        <div style={ leftBtn }>
          {props.previousExists ?
            <button style={ backBtn } className='btn'>back</button>
          : ''}
        </div>
        <div style={ rightBtn }>
          {props.isLast ?
            <button style={ completeBtn } className='btn'>complete</button> :
            <button style={ nextBtn } className='btn'>next</button>
          }
        </div>
      </div>
    </div>
  </div>
);

const outerStyle = {
  width: '300px',
  margin: 'auto',
  textAlign: 'center'
};

const innerStyle = {
  margin: '100px 0 0 0',
  textAlign: 'center'
};

const imageStyle = { width: '100px' };
const questionStyle = {
  margin: '30px 0 0 0',
  width: '100%'
};
const inputStyle = {
  margin: '40px 0 0 0',
  padding: '2% 5%',
  width: '90%',
  height: '40px',
  fontSize: '16px'
};
const buttonSection = {
  margin: '40px 0 0 0',
  position: 'relative'
};
const leftBtn = {
  position: 'absolute',
  left: 0
};
const rightBtn = {
  position: 'absolute',
  right: 0
};

export default Widget
