import React from 'react';
import './Widget.css';

const outer = {
  height: '100vh',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center'
};

const inner = {
  flex: 'none',
  maxWidth: '90%',
  textAlign: 'center',
  position: 'relative',
  padding: '50px'
};


const answer = {
  padding: '2% 5%',
  minWidth: '300px',
  minHeight: '40px',
  width: '90%',
  height: '40px',
  fontSize: '16px',
  border: '1px solid rgba(100,100,100,1)'
}

const Widget = (props) => {
  console.log(window.innerWidth)
  return (
    <div style={ outer }>
      <div className='inner-padding' style={ inner }>


          <section style={{ minHeight: '120px' }}>
            <img className='image-style' src={props.image} alt={props.alt}/>
          </section>

          <section>
            <h2 className='question'>{props.question}</h2>
            <input style={ answer } type='text' placeholder={props.placeholder}/>
          </section>

          <section className='buttons'>
            <button className='btn-container back-btn btn'>
              { props.previousExists ? 'Back' : '' }
            </button>

            <button className='btn-container next-btn btn'>
              { props.isLast ? 'Complete' : 'Next' }
            </button>

          </section>

      </div>
    </div>
  );
}

export default Widget
