import React from 'react';
import './Widget.css';

const outerStyle = {
  width: '300px',
  margin: 'auto',
  textAlign: 'center'
};

const innerStyle = {
  margin: '100px 0 0 0',
  textAlign: 'center'
};

const imageStyle = { width: '100px' };
const questionStyle = {
  margin: '40px 0 0 0',
  width: '100%'
};
const inputStyle = {
  margin: '20px 0 0 0',
  width: '100%'
};
const buttonSection = { margin: '40px 0 0 0' };

const Widget = (props) => (
  <div style={ outerStyle }>
    <div style={ innerStyle }>
      <img style={ imageStyle } src={props.image} alt={props.alt}/>
      <h2 style={ questionStyle }>{props.question}</h2>
      <input style={ inputStyle } type='text' />
      <div style={ buttonSection }>
        {props.previousExists ? <button className='btn'>back</button> : ''}
        {props.isLast ? <button className='btn'>next</button> : <button className='btn'>complete</button>}
      </div>
    </div>
  </div>
);

export default Widget
