import React, { Component } from 'react';
import Widget from './Widget';
import cards from '../data/cards';

class Wizard extends Component {
  constructor(props) {
    super(props);
    this.state = { current: 0 }
    // this.isLastQuestion = this.isLastQuestion.bind(this)
    // this.previousExists = this.previousExists.bind(this)
    this.handleNext = this.handleNext.bind(this)
    this.handlePrevious = this.handlePrevious.bind(this)
  }

  // isLastQuestion() {
  //   if (cards.length === this.state.current - 1) { return true }
  // }
  // previousExists() {
  //   if (this.state.current === 0) { return true }
  // }
  handleNext() {
    this.setState({ current: this.state.current + 1 })
  }
  handlePrevious() {
    this.setState({ current: this.state.current -1 })
  }

  render() {
    console.log(cards)
    return (
      <div>
          <Widget
            question={cards[this.state.current].question}
            image={cards[this.state.current].image}
            placeholder={cards[this.state.current].placeholder}
            isLast={() => (cards.length === this.state.current - 1)}
            previousExists={this.previousExists()}
            handleNext={this.handleNext()}
            handlePrevious={this.handlePrevious()}
          />
      </div>
    );
  }
}

export default Wizard;
