import React from 'react';
import './Widget.css';

const outer = {
  height: '100vh',
  width: '300px',
  margin: 'auto',
};

const inner = {
  position: 'relative',
  top: '25%',
  transform: 'perspective(1px) translateY(-50%)'
};



const questionStyle = {
  margin: '30px 0 0 0',
  width: '100%'
};

const inputStyle = {
  margin: '20px 0 0 0',
  padding: '2% 5%',
  width: '90%',
  height: '40px',
  fontSize: '16px'
};

const leftBtn = {
  position: 'absolute',
  left: 0
};

const rightBtn = {
  position: 'absolute',
  right: 0
};


const Widget = (props) => (
  <div style={ outer }>
    <div style={ inner }>

      <section className='image-section' style={{ minHeight: '120px' }}>
        <img className='image-style' src={props.image} alt={props.alt}/>
        &nbsp;
      </section>

      <section className='input-section'>
        <h2 style={ questionStyle }>{props.question}</h2>
        <input style={ inputStyle } type='text' placeholder={props.placeholder}/>
      </section>

      <section className='button-section'>
        <div style={ leftBtn }>
          {props.previousExists ?
            <button className='btn backBtn'>Back</button>
          : ''}
        </div>
        <div style={ rightBtn }>
          {props.isLast ?
            <button className='btn nextBtn'>Complete</button> :
            <button className='btn nextBtn'>Next</button>
          }
        </div>
      </section>

    </div>
  </div>
);

export default Widget
