import React from 'react';
import './Widget.css';

const outer = {
  height: '100vh',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center'
};

const inner = {
  flex: 'none',
  maxWidth: '700px',
  textAlign: 'center',
  border: '3px solid rgba(166,128,184,1)',
  padding: '100px',
  position: 'relative',
  maxWidth: '90%'
};


const answer = {
  padding: '2% 5%',
  minWidth: '300px',
  minHeight: '40px',
  width: '90%',
  height: '40px',
  fontSize: '16px'
}

const Widget = (props) => (

  <div style={ outer }>
    <div style={ inner }>

      <div style={ borderBox }>
        <section style={{ minHeight: '120px' }}>
          <img className='image-style' src={props.image} alt={props.alt}/>
        </section>

        <section>
          <h2 className='question'>{props.question}</h2>
          <input style={ answer } type='text' placeholder={props.placeholder}/>
        </section>

        <section className='buttons'>
          <button className='btn-container back-btn btn'>
            { props.previousExists ? 'Back' : '' }
          </button>

          <button className='btn-container next-btn btn'>
            { props.isLast ? 'Complete' : 'Next' }
          </button>

        </section>
      </div>

    </div>
  </div>
);

export default Widget
