import React from 'react';
import './Widget.css';

const Widget = (props) => {

  const inner = {
    flex: 'none',
    maxWidth: '90%',
    textAlign: 'center',
    position: 'relative',
    padding: '50px'
  };

  if (window.innerWidth > 400) {
    inner.border = 'rgba(166,128,184,1)'
  }

  return (
    <div className='outer'>
      <div className='inner-padding' style={ inner }>

          <section style={{ minHeight: '120px' }}>
            <img className='image-style' src={props.image} alt={props.alt}/>
          </section>

          <section>
            <h2 className='question'>{props.question}</h2>
            <input style={ answer } type='text' placeholder={props.placeholder}/>
          </section>

          <section className='buttons'>
            <button className='btn-container back-btn btn'>
              { props.previousExists ? 'Back' : '' }
            </button>

            <button className='btn-container next-btn btn'>
              { props.isLast ? 'Complete' : 'Next' }
            </button>

          </section>

      </div>
    </div>
  );
}

export default Widget
