import React from 'react';
import './Widget.css';

const outer = {
  height: '100vh',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
};

const inner = {
  flex: 'none',
  maxWidth: '700px',
  textAlign: 'center'
};


const Widget = (props) => (

  <div style={ outer }>
    <div style={ inner }>

      <section className='image-section' style={{ minHeight: '120px' }}>
        <img className='image-style' src={props.image} alt={props.alt}/>
      </section>

      <section className='qa-section'>
        <h2 className='question'>{props.question}</h2>
        <input className='answer' type='text' placeholder={props.placeholder}/>
      </section>

      <section className='button-section'>
        <div className='left-btn'>
          {props.previousExists ?
            <button className='btn back-btn'>Back</button>
          : ''}
        </div>
        <div className='right-btn'>
          {props.isLast ?
            <button className='btn next-btn'>Complete</button> :
            <button className='btn next-btn'>Next</button>
          }
        </div>
      </section>

    </div>
  </div>
);

export default Widget
