import React from 'react';
import './Widget.css';

const outer = {
  height: '100vh',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
};

const inner = {
  flex: 'none',
  maxWidth: '700px',
  textAlign: 'center',
  border: '3px solid rgba(166,128,184,1)',
  padding: '100px',
  position: 'relative'
};

const answer = {
  padding: '2% 5%',
  minWidth: '300px',
  minHeight: '40px',
  width: '90%',
  height: '40px',
  fontSize: '16px'
}

const btnSection = {
  margin: 'auto',
}
const backBtn = {
  width: '100px',
  height: '40px',
  fontSize: '12px',
  float: 'right',
  background: 'rgba(213,232,212,1)',
  border: '2px solid rgba(130,179,102,1)'
}
const nextBtn = {
  width: '100px',
  height: '40px',
  fontSize: '12px',
  float: 'left',
  background: 'rgba(213,232,212,1)',
  position: 'relative',
  border: '2px solid rgba(192,217,178,1)',
  margin: '8px'
}

const Widget = (props) => (

  <div style={ outer }>
    <div style={ inner }>

      <section style={{ minHeight: '120px' }}>
        <img className='image-style' src={props.image} alt={props.alt}/>
      </section>

      <section>
        <h2 className='question'>{props.question}</h2>
        <input style={ answer } type='text' placeholder={props.placeholder}/>
      </section>

      <section style={ btnSection }>

          {props.previousExists ?
            <button style={ backBtn }>Back</button>
          : ''}

          {props.isLast ?
            <button style={ nextBtn }>Complete</button> :
            <button style={ nextBtn }>Next</button>
          }

      </section>

    </div>
  </div>
);

export default Widget
