import React from 'react';
import './Widget.css';

const outerStyle = {
  width: '300px',
  margin: 'auto',
  textAlign: 'center'
}

const imageStyle = {
  width: '100px'
}

const Widget = (props) => (
  <div style={ outerStyle }>
    <div style={ innerStyle }>
      <img style={ imageStyle } src={props.image} alt={props.alt}/>
      <h4>{props.question}</h4>
      <input />
      {props.previousExists ? <button>previous</button> : ''}
      {props.isLast ? <button>next</button> : <button>complete</button>}
    </div>
  </div>
);

export default Widget
