import React from 'react';
import './Widget.css';

const outer = {
  height: '100vh',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
};

const inner = {
  flex: 'none',
  maxWidth: '700px',
  textAlign: 'center',
  border: '3px solid rgba(166,128,184,1)',
  padding: '100px',
  position: 'relative'
};

const answer = {
  padding: '2% 5%',
  minWidth: '300px',
  minHeight: '40px',
  width: '90%',
  height: '40px',
  fontSize: '16px'
}

const btnSection = { display: 'flex' }

const leftBtn = {
  position: 'absolute',
  left: 0
}
const rightBtn = {
  position: 'absolute',
  right: 0
}


const Widget = (props) => (

  <div style={ outer }>
    <div style={ inner }>

      <section style={{ minHeight: '120px' }}>
        <img className='image-style' src={props.image} alt={props.alt}/>
      </section>

      <section>
        <h2 className='question'>{props.question}</h2>
        <input style={ answer } type='text' placeholder={props.placeholder}/>
      </section>

      <section style={ btnSection }>
        <div style={ leftBtn }>
          {props.previousExists ?
            <button className='btn back-btn'>Back</button>
          : ''}
        </div>
        <div className='right-btn'>
          {props.isLast ?
            <button className='btn next-btn'>Complete</button> :
            <button className='btn next-btn'>Next</button>
          }
        </div>
      </section>

    </div>
  </div>
);

export default Widget
